INSERT INTO product(product) VALUES (jsonb_build_object('name', 'GUND Cozys Collection Cow, Stuffed Animal for Ages 1 and Up, Spring Decor Plush Toy, Cream/Blue, 10”', 'description', jsonb_build_array('COZYS COLLECTION PLUSH COW: This adorable stuffed cow sits at 10 inches tall and features soft, floppy limbs made for hugging and specially rippled, cream-colored plush fur with navy blue spots. The Cozys Cow also features velvety-soft beige horns and a tuft of fluffy navy fur on its tail, with a slightly under-stuffed body that makes hugs feel even sweeter.', 'SOFT & HUGGABLE: This plush toy is made from soft, huggable materials and features surface-washable construction for easy cleaning. Each Cozys Collection stuffed animal is appropriate for ages 1 and up and ships in a protective poly bag.', 'GIFT GUND FOR EASTER: Fill your Easter basket with GUND plushies! From the cutest Easter bunny to classic teddy bears to Peter Rabbit and Hello Kitty plush, our super soft, squishy toys make great Easter decorations and Easter gifts for all ages.', 'QUALITY CUDDLES: Our award-winning bears and kids toys appeal to animal lovers everywhere, known for unmatched quality and huggably soft designs. From toddler toys to kawaii plush for all ages, GUND toys are perfect for play, collecting and cuddling.', 'PREMIER PLUSH: As one of America’s first soft toy makers, GUND has crafted unique and classic teddy bears and plush animals for 125 years. Today we proudly carry this legacy and stand by our kids toys, known globally for quality and innovation.', 'Includes: 1 GUND Cozys Collection Plush, Poly Bag', 'Covered by the Spin Master Care Commitment. See below for full details'), 'price', 20.53, 'rating', 4.9, 'image', jsonb_build_array('https://m.media-amazon.com/images/I/81nZwseiM7L._AC_SL1500_.jpg', 'https://m.media-amazon.com/images/I/81NjygmA9kL._AC_SL1500_.jpg', 'https://m.media-amazon.com/images/I/815S-KtzqgL._AC_SL1500_.jpg', 'https://m.media-amazon.com/images/I/81apygSCnDL._AC_SL