INSERT INTO product(product) VALUES (jsonb_build_object('name', 'Silver Buffalo Rick and Morty Character Phrases 4-Pack Mini Glass Set, 1.5-Ounce', 'description', jsonb_build_array('RICK AND MORTY: Each mini glass is designed with artwork from Rick and Morty, that feature phrases and characters from the show including Rick, Morty, Pickle Rick and Mr. Meeseeks.', '4-PIECE SET: This mini glass set comes with 4 glasses 1.5 oz each. Perfect for daily use to enjoy with friends, or as a collector’s item.', 'INSTRUCTIONS FOR CARE: Top-rack dishwasher safe or hand-wash. AVOID bleach and chlorine products. NOT microwave-safe.', 'GREAT GIFT FOR RICK & MORTY FANS: Great for your next Rick and Morty birthday party, Christmas or housewarming gift. This glass set is perfect for children, adults and Rick and Morty fans of all ages.', 'OFFICIAL MERCH: Have confidence knowing you are purchasing officially licensed Rick and Morty products from Silver Buffalo. Find more from your favorite movies, series and characters.'), 'price', 13.05, 'rating', 4.8, 'image', jsonb_build_array('https://m.media-amazon.com/images/I/81RSk1yOg1L._AC_SL1500_.jpg', 'https://m.media-amazon.com/images/I/71FXSDLzfIL._AC_SL1500_.jpg', 'https://m.media-amazon.com/images/I/61YdcPQc4kL._AC_SL1200_.jpg', 'https://m.media-amazon.com/images/I/81J5Y+Y4k5L._AC_SL1500_.jpg')));
INSERT INTO product(product) VALUES (jsonb_build_object('name', 'Hasbro Gaming Monopoly: Rick and Morty Edition Board Game, Cartoon Network Game for Families and Teens 17+, Includes Collectible Monopoly Tokens (Amazon Exclusive)', 'description', jsonb_build_array('MONOPOLY GAME INSPIRED BY RICK AND MORTY: In this edition of the Monopoly board game for 2-6 players, you gallivant to victory as you buy, sell, and trade locations from the Rick and Morty Multiverse', 'INCLUDES 6 COLLECTIBLE MONOPOLY TOKENS: Move around the board as the Council of Ricks Badge, a Meeseeks Box, an Oportal Gun, a Plumbus, Ricks Car or Snuffles Helmet', 'INSPIRED BY CARTOON NETWORK SERIES: The Monopoly: Rick and Morty Edition game features familiar locations from the Multiverse including Cromulon Dimension, Gazorpazorp and Planet Squanch', 'WUBBA LUBBA DUB DUB: With InterDimensional TV cards, Get Schwifty cards, Flooble Cranks, Gooble Boxes, Flurbo currency, and language from the series, this game is a must-have for Rick and Morty fans', 'GAME FOR ADULTS AND TEENS 17 plus: This twist on classic Monopoly gameplay is a great choice for a family game night. It''s a fun birthday gift or holiday gift for Rick and Morty and [adult swim] enthusiasts'), 'price', 34.60, 'rating', 4.7, 'image', jsonb_build_array('https://m.media-amazon.com/images/I/71mH+6jUHhL._AC_SL1500_.jpg', 'https://m.media-amazon.com/images/I/814u-Q7U17L._AC_SL1500_.jpg', 'https://m.media-amazon.com/images/I/81nb0-4ws-L._AC_SL1500_.jpg', 'https://m.media-amazon.com/images/I/81OwSi9HNrL._AC_SL1500_.jpg')));
INSERT INTO product(product) VALUES (jsonb_build_object('name', 'Anime Neon Sign Rick Neon Signs for Wall Dimmable Morty Cartoon LED Signs for Bedroom Game Room Man Cave Party Light Up Signs Neon Wall Light Decor Christmas Gift for Teens', 'description', jsonb_build_array('😜Vivid and Vibrant: Our anime neon signs are themed after beloved cartoon characters, and anime neon lights instantly catch the eye and bring a pop of color and fun atmosphere to any space.', '🔆Adjustable Brightness: One of the standout features of this anime LED wall sign is adjustable brightness. With multiple brightness levels adjustable via a switch controller on the LED sign’s power cord, you have complete control over the neon light intensity. Whether you prefer soft ambient lighting or a bold statement piece, our anime neon lights for wall can adjust to your mood or environment.', '✅Safe and Durable: This anime neon light signs wall decor adopt LED lighting technology and are made of safe and reliable silicone strips and strong acrylic sheets. Our LED light sign is more durable and consumes less energy. It can be used in any room without any risk of glass breakage like traditional glass neon lights.', '💖Versatile Placement: Anime light up signs for wall decor can be placed in a variety of ways. The anime neon wall signs comes with pre-drilled holes in the acrylic panel and is powered by USB power so you can easily mount it on the wall or hang it in your window.Whether you want to spruce up your bedroom, living room, game room, or even your office space, the glow neon sign can blend into a variety of environments.', '🎁Fantastic Gift Option: Looking for a unique and memorable gift? This anime LED signs for wall is ideal. Perfect for birthdays, holidays, or special occasions, they make a thoughtful gift for fans of this animated series. Stylish LED technology combined with classic characters will impress.'), 'price', 37.99, 'rating', 4.8, 'image', jsonb_build_array('https://m.media-amazon.com/images/I/71JCwdnOaCL._AC_SL1500_.jpg', 'https://m.media-amazon.com/images/I/71B0Y+tg8sL._AC_SL1500_.jpg', 'https://m.media-amazon.com/images/I/61jV-CCbQqL._AC_SL1500_.jpg', 'https://m.media-amazon.com/images/I/71Jo8WJT0FL._AC_SL1500_.jpg')));
INSERT INTO product(product) VALUES (jsonb_build_object('name', 'USAopoly, Inc. Risk Rick and Morty Risk Game | Based on The Popular Adult Swim TV Show Rick & Morty | Official Rick and Morty Merchandise | Classic Risk Board Game Themed for Rick Morty Series', 'description', jsonb_build_array('Rick and Morty Risk brings a twist to this classic game of strategic conquest', 'Features five power driven factions which include Mythologs, Robot Dogs, Gazorpians, Post-Apocalyptic People, and of course, the U.S. Government', 'As a leader of a faction you will have the ability to grant extra power in the territory you occupy', 'Includes 1 Main Game Board, 1 Rick’s Battery Game Board, 5 Leader Tokens, 5 Stands for Leaders, 1 Rick’s Ship Token, 4 Portal Tokens, 10 Neutral Unit Tokens, 5 Dice, 225 Faction Unit Tokens, and 83 Cards', '3-5 Players | Ages 17+ | 60+ Min Play Time'), 'price', 48.45, 'rating', 4.6, 'image', jsonb_build_array('https://m.media-amazon.com/images/I/71mn+r1KfoL._AC_SL1000_.jpg', 'https://m.media-amazon.com/images/I/61imQzugseL._AC_SL1000_.jpg', 'https://m.media-amazon.com/images/I/71P1YdkCVUL._AC_SL1000_.jpg', 'https://m.media-amazon.com/images/I/71OpwlltekL._AC_SL1000_.jpg')));
INSERT INTO product(product) VALUES (jsonb_build_object('name', 'Silver Buffalo Rick and Morty Floating Portal Ceramic Ramen Noodle Rice Bowl with Chopsticks and Spoon, Microwave Safe, 20 Ounces', 'description', jsonb_build_array('RAMEN BOWL: Step up your ramen game with this awesome Rick and Morty Ceramic Ramen Bowl with Chopsticks and spoon.', 'MULTIPLE USES: This ceramic ramen bowl has a wide mouth opening and can be used for, rice, soup, noodles, ice cream, fruit, cereal, or whatever your heart desires', 'DURABLE AND STURDY: Perfect for hot or cold food, BPA-free, microwave-safe, and top-shelf dishwasher safe', 'OFFICIALLY LICENSED: Have confidence knowing you are purchasing officially licensed Rick and Morty Floating Portal Ceramic Ramen Bowl from Silver Buffalo', 'MAKES A GREAT GIFT: Makes a great gift for all the Rick and Morty fans in your life, now they can feel like they are having ramen with their favorite characters. Great for any occasion whether it is Christmas, a Birthday, or a Housewarming gift'), 'price', 18.83, 'rating', 4.8, 'image', jsonb_build_array('https://m.media-amazon.com/images/I/71g1CRCjgcL._AC_SL1500_.jpg', 'https://m.media-amazon.com/images/I/81COJpx3HGL._AC_SL1500_.jpg', 'https://m.media-amazon.com/images/I/81b2uDXZXZL._AC_SL1500_.jpg', 'https://m.media-amazon.com/images/I/61sEf7GFF4L._AC_SL1500_.jpg')));
