INSERT INTO product(product) VALUES (jsonb_build_object('name', 'Gibson Elite Mayfair Bay Embossed Double Bowl Dinnerware Set, Service for 4 (16pcs), Blue', 'description', jsonb_build_array('WHAT''S IN THE BOX: Service for 4 that includes 4 of each of the following: 10.75" Dinner Plates, 8.3" Dessert Plates, 6" Cereal Bowls, 8.5” Dinner Bowls', 'CARE INSTRUCTIONS: beautiful dishes that are also dishwasher and microwave safe', 'STONEWARE/EMBOSSED REACTIVE GLAZE: reactive refers to a technique how multiple colors within the glaze react together to create a dreamlike, vibrant quality to the colors and hues. As a result of the reactive nature of the glaze, each stoneware piece is unique. The high firing temperature, thick build, and mix of all-natural clays, makes stoneware an incredibly durable material.', 'FAMILY OWNED: Gibson is an industry-leading producer of tabletop and housewares products based in Los Angeles, California. Family-owned for over 40 years, we’ve developed a family of brands that thrive off relentless commitment to style, innovation, and value for our consumers around the world', 'GIBSON ELITE: the premier brand for reactive glaze, hand-painted ceramics, and whiteware, Gibson Elite brings artisan craftsmanship to the everyday'), 'price', 69.99, 'rating', 4.6, 'image', jsonb_build_array('https://m.media-amazon.com/images/I/81Wr29mZTPL._AC_SX679_.jpg', 'https://images-na.ssl-images-amazon.com/images/I/810UWOFp29L._AC_UL348_SR348,348_.jpg', 'https://images-na.ssl-images-amazon.com/images/I/71f6drMnaaL._AC_UL348_SR348,348_.jpg', 'https://images-na.ssl-images-amazon.com/images/I/714xpQK35JL._AC_UL330_SR330,330_.jpg', 'https://images-na.ssl-images-amazon.com/images/I/81GXlQVbpZL._AC_UL330_SR330,330_.jpg', 'https://images-na.ssl-images-amazon.com/images/I/61P0yLZofkL._AC_UL330_SR330,330_.jpg', 'https://images-na.ssl-images-amazon.com/images/I/61AfH9a8pAL._AC_UL330_SR330,330_.jpg')));
INSERT INTO product(product) VALUES (jsonb_build_object('name', 'Ceramic Dinnerware Sets of 6,Plates and Bowls Sets,Handmade Reactive Glaze Dishes Set,Chip Resistant and Scratch Resistant | Oven&Dishwasher & Microwave Safe-Blue(18PCS)', 'description', jsonb_build_array('【 ELEGANT AND STYLISH DESIGN】This dinnerware set, with its vibrant colors and unique surface treatment, leads the dining trend. With a lively color palette and captivating surface treatment, it creates a warm and enchanting atmosphere. The eye-catching reactive glazes and rich, vivid hues not only soothe the senses but also enhance your dining experience. Another distinctive feature of our product is its ability to be placed in the oven, providing you with more cooking possibilities.', '【LEAD FREE EAT SAFTY】Our plates and bowls set is made of biodegradable material and features an all-natural glaze that is lead-free, cadmium-free. The set is fired at a high temperature of 2340℉ for 17 hours to ensure its durability and safety. With this dinnerware set, you''ll never have to worry about any health problems', '【UNIQUE AND SCRATCH RESISTANT】Our plates are fired with two different glazes,The reactive glaze make every plate is unique,Since our glaze is all-natural reactive finish, the surface of plates would change color on lighting,So Our glaze is also extra strong to prevent scratches from your knives and forks ❤(Please note dark marks may occur silverware, which are not scratches,it is very easy to remove.)', '【SERVICE FOR 6 】6 pcs of 10.5" dinner plates, 6 pcs of 8.5" salad/appetizer/dessert plates, and 6 pcs of 26 oz soup/cereal/ceramic bowls. It makes a great housewarming gift for newlyweds who appreciate quality and style, or for anyone who has recently moved into a new home or apartment. With this set, you can enjoy your meals in style and create beautiful memories with your loved ones.', '【GREAT GIFT OPTION】It''s packaged in a well-wrapped box ,makes it an ideal gift for housewarming parties, weddings, Mother''s Day, Father''s Day, or Christmas. Are you sure you don''t want to order a set? ❤ We prioritize exceptional customer service, as you are important to us.❤ We offer free refunds or replacements for any defective products.'), 'price', 99.99, 'rating', 4.7, 'image', jsonb_build_array('https://m.media-amazon.com/images/I/81jgetrp87L._AC_SX679_.jpg', 'https://images-na.ssl-images-amazon.com/images/I/7137euQGxUL._AC_UL348_SR348,348_.jpg', 'https://images-na.ssl-images-amazon.com/images/I/71NU59oeQpL._AC_UL348_SR348,348_.jpg', 'https://images-na.ssl-images-amazon.com/images/I/71uvMsu12KL._AC_UL330_SR330,330_.jpg', 'https://images-na.ssl-images-amazon.com/images/I/714xpQK35JL._AC_UL330_SR330,330_.jpg', 'https://images-na.ssl-images-amazon.com/images/I/81xWVEEwnQL._AC_UL330_SR330,330_.jpg', 'https://images-na.ssl-images-amazon.com/images/I/81GXlQVbpZL._AC_UL330_SR330,330_.jpg')));
